        cout << str[(depth + 1) % 2] << endl;
        exit(0);
    } else {
        rep(i, n) if (a[i] % 2) a[i]--;
        lli g = a[0];
        rep(i, n) g = __gcd(g, a[i]);
        rep(i, n) a[i] /= g;
        solve(a, depth + 1);
    }
}
