bool solve(vl a) {
    int n = SZ(a);
    int kek = accumulate(ALL(a), 0LL) - n;
    if (kek & 1)
        return true;
    if (*min_element(ALL(a)) == 1)
        return false;
    int cntodd = 0;
    for (auto x : a) {
        cntodd += x & 1;
    }
    if (cntodd == 1) {
        int g = 0;
        for (auto & x : a) {
            if (x & 1) 
                x--;
            g = __gcd(x, g);
        }

        for (auto & x : a) {
            x /= g;
        }

        return !solve(a);
    }
    return false;
}

