bool solve(vector<int> a){
    int n=a.size();
    ll sum=0;
    for(int i=0;i<n;i++){
        sum+=a[i]-1;
    }
    if(sum==0)return false;
    if(sum%2)return true;
    int odd=0;
    for(int i=0;i<n;i++){
        if(a[i]%2)odd++;
    }
    if(odd>=2)return false;
    int k=-1;
    for(int i=0;i<n;i++){
        if(a[i]%2&&a[i]>=2)k=i;
