    if(k==-1)return false;
    a[k]--;
    int g=a[0];
    for(int i=0;i<n;i++){
        g=__gcd(a[i],g);
    }
    for(int i=0;i<n;i++){
        a[i]/=g;
    }
    return !solve(a);
}
