bool solve()
{
	if(n==1)return ar[1]!=1;
	ll sum=0;
	int now=0;
	int v;
	for(int i=1;i<=n;i++)
	{
		sum+=ar[i];
		if(ar[i]&1)
			now++,v=i;
	}
	if((sum-n)%2)return 1;
	if(now!=1 || ar[v]==1)return 0;
	ar[v]--;
	int t=ar[v];
	for(int i=1;i<=n;i++)t=__gcd(t,ar[i]);
	for(int i=1;i<=n;i++)ar[i]/=t;
	return 1-solve();
}

