int n, s[100005], oc, g;

int gcd(int x, int y){
	if(!y) return x;
	return gcd(y, x % y);
}

int chk(int player){
	int oc = 0;

	FI(i, 1, n) if(s[i] & 1) oc++;
	if((n - oc) % 2 == 1) return player;
	else if(oc == 1){
		int g = 0;

		FI(i, 1, n) if(s[i] & 1){
			if(s[i] == 1) return 3 - player;
			else s[i]--;
		}


		FI(i, 1, n) g = gcd(g, s[i]);
		assert(g > 1);
		FI(i, 1, n) s[i] /= g;

		return chk(3 - player);
	}
	else return 3 - player;
}

