#include<bits/stdc++.h>
#define N 100005
using namespace std;
int n,val[N];
bool judge(){
    if(n==1) return val[1]!=1;
    int num[2]={0,0};
    for(int i=1;i<=n;i++) num[val[i]%2]++;
    if(num[0]%2==1) return true;
    if(num[0]%2==0&&num[1]>1) return false;
    for(int i=1;i<=n;i++)
        if(val[i]%2==1) val[i]--;
    int gcd=val[1];
    for(int i=1;i<=n;i++) gcd=__gcd(gcd,val[i]);
    for(int i=1;i<=n;i++) val[i]/=gcd;
    return !judge();
}
int main(){
    scanf("%d",&n);
    for(int i=1;i<=n;i++) scanf("%d",&val[i]);
    if(judge()) puts("First");
    else puts("Second");
    return 0;
}

#include <bits/stdc++.h>
#define LL long long
using namespace std;
int n,tot;
LL a[100010];
LL gcd(LL x,LL y)
{
    if (x%y == 0) return y;
    else return gcd(y,x%y);
}
void init()
{
    for (int i = 0;i < n;i++) if (a[i]&1) a[i]--;
    LL g = a[0];
    for (int i = 1;i < n;i++) g = gcd(g,a[i]);
    for (int i = 0;i < n;i++) a[i] /= g;
    tot = 0;
    for (int i = 0;i < n;i++) if (!(a[i]&1)) tot++;
}
int dfs()
{
    if (tot&1) return 1;
    else {
        if (n-tot >= 2) return 0;
        else {
            init();
            return dfs()^1;
        }
    }
}
int main()
{
    cin >> n;
    for (int i = 0;i < n;i++)
    {
        scanf("%lld",&a[i]);
        if (!(a[i]&1)) tot++;
    }
    if (dfs()) printf("First\n");
    else printf("Second\n");
    return 0;
}

