function<LL(LL, LL)> gcdlambda = [](LL n, LL m) { return __gcd(n, m); };
bool solveA(vector<LL>& A) {
  int N = A.size();
  LL diff = 0;
  for (int i = 0; i < N; i++) diff += A[i] - 1;
  if (diff % 2 == 1) return true;

  bool cur = false;
  while (true) {
    int t = count_if(A.begin(), A.end(), [&](int x) {
      if (x % 2 == 0) return true;
      return false;
    });
    if (t < N - 1) return false ^ cur;
    for (int i = 0; i < N; i++)
      if (A[i] % 2 == 1) A[i]--;
    LL g = A[0];
    for (int i = 0; i < N; i++) g = gcdlambda(g, A[i]);
    for (int i = 0; i < N; i++) A[i] /= g;
  }
}

