bool dfs(vector<int> A){
	int c = 0;
	for(int i=0;i<A.size();i++){
		c += (A[i]-1)%2;
		c%=2;
	}

	if(c==1)return true;
	c += A.size();
	c %= 2;
	if(c==0)return false;

	int ind = -1;
	for(int i=0;i<A.size();i++){
		if(A[i]%2==1){
			if(A[i]==1)return false;
			if(ind!=-1)return false;
			ind = i;
			A[i]--;
			A[i]/=2;
		}
		else{
			A[i]/=2;
		}
	}

	if(dfs(A))return false;
	return true;
}

bool dfs2(vector<int> A){
	int c = A[0]+A[1]-2;
	c %= 2;
	if(c==1)return true;
	return false;
}

