bool win(const vector<int>& v) {
	LL sum = accumulate(v.begin(), v.end(), 0ll);
	if (*min_element(v.begin(), v.end()) == 1) {
		return sum % 2 != v.size() % 2;
	}

	if (sum % 2 != v.size() % 2) return true;

	int odd = 0;
	for (int i : v) {
		if (i % 2 == 1) ++odd;
	}

	if (odd > 1) return false;
	vector<int> w(v);
	for (int& i : w) {
		i = i / 2 * 2;
	}

	int g = 0;
	for (int i : w) {
		g = gcd(g, i);
	}

	for (int& i : w) {
		i /= g;
	}
	return !win(w);
}

