int solve(int n) {
	bool notOne = false;
	int cnt0 = 0;
	int cnt1 = 0;
	FOR(i,0,n) {
		if ((a[i]&1) == 0) {
			cnt0++;
		} else {
			cnt1++;
		}
		if (a[i] > 1) {
			notOne = true;
		}
	}
	if (!notOne) {
		return 0;
	}
	if (cnt0 & 1) {
		return 1;
	}
	if (cnt1 >= 2) {
		return 0;
	}
	FOR(i,0,n) {
		if (a[i] & 1) {
			if (a[i] == 1) {
				return 0;
			}
			a[i]--;
			int g = a[0];
			FOR(j,0,n) {
				g = gcd(g, a[j]);
			}
			FOR(j,0,n) {
				a[j] /= g;
			}
			int val = solve(n);
			return (val ^ 1);
		}
	}
	return -1;
}

