// X.order_of_key(k) = number of items in X < k (strict lt)

ll gcd(ll a, ll b) {
    if (b > a) return gcd(b, a);
    if (b == 0) return a;
    return gcd(b, a % b);
}

bool solve(int N, vll& A) {
    ll S = 0;
    for (ll a : A) S += a;

    if (N % 2 == 0) {
        if (S % 2 == 0) return false;
        else return true;
    } else {
        int num_odd = 0;
        for (ll a : A) num_odd += (a % 2);

        if (S % 2 == 0) return true;
        else if (num_odd > 1) return false;
        else {
            vll B(A);
            for (int i=0;i<N;i++){
                if (B[i] % 2 == 1) B[i]--;
            } 

            ll g = B[0];
            for (int i=1;i<N;i++) g = gcd(g, B[i]);

            for (int i=0;i<N;i++) {
                B[i]/=g;
            }
            return !solve(N, B);
        }

    }
}

