    int N;
    cin >> N;

    vll A(N);

    for (int i=0;i<N;i++) cin >> A[i];

    if (N == 1) cout << "Second" << endl;

    if (solve(N, A)) {
        cout << "First" << endl;
    } else {
        cout << "Second" << endl;
    }

    return 0;

}

