bool f(vector<int> &A) {
    int N = A.size();
    int sum = 0;
    for (int i = 0; i < N; i++) {
        sum += A[i] - 1;
    }
    if (sum % 2 == 1) return false;
    vector<int> L(N), R(N);
    L[0] = A[0], R[N - 1] = A[N - 1];
    for (int i = 1; i < N; i++) {
        L[i] = __gcd(L[i - 1], A[i]);
    }
    for (int i = N - 2; i >= 0; i--) {
        R[i] = __gcd(R[i + 1], A[i]);
    }
    for (int i = 0; i < N; i++) if (A[i] >= 2) {
        int t = A[i] - 1;
        if (i != 0) t = __gcd(t, L[i - 1]);
        if (i != N - 1) t = __gcd(t, R[i + 1]);
        if (t != 1) {
            A[i] -= 1;
            for (auto &a : A) a /= t;
            bool ret = f(A);
            for (auto &a : A) a *= t;
            A[i] += 1;
            if (ret) return false;
        }
    }
    return true;
}
