int gcd(int x,int y)
{
	int tmp;
	while (x)
	{
		tmp=x%y;
		x=y,y=tmp;
	}
	return x;
}

int Solve()
{
	if (n==1) return a[1]%2==0;

	bool flag=true;
	for (int i=1;i<=n;i++) if (a[i]!=1) flag=false;
	if (!flag) return 0;

	int cnt=0;
	for (int i=1;i<=n;i++) if (!(a[i]&1)) cnt++;
	if (cnt==n) 
	{
		a[1]--;
		return Solve()^1;
	}
	else 
	{
		if (cnt&1) return 1;
		else
		{
			if (cnt!=n-1) return 0;
			bool flag=false;
			for (int i=1;i<=n;i++) if (a[i]>1 && (a[i]&1))
			{
				flag=true;
				a[i]--;
				int d=a[1];
				for (int j=2;j<=n;j++) d=gcd(d,a[j]);
				for (int j=1;j<=n;j++) a[j]/=d;
				return Solve()^1;
			}
			if (!flag) return 0;
		}
	}
}

