bool F()
{
	int odd = std::count_if(a + 1, a + N + 1, [] (int x) { return x & 1; }), even = N - odd;
	if (even & 1)
		return true;
	if (odd == 1)
	{
		(*std::find_if(a + 1, a + N + 1, [] (int x) { return x & 1; }))--;
