bool solve() {
  int n = read<int>();
  vector<int> A(n);
  rep(i, n) A[i] = read<int>();

  bool self = true;

  while (1) {
    // 偶数の数、奇数の数、奇数のindex
    int no=0, ne=0, oi = -1;

    rep(i, n) {
      int a = A[i];
      if (a % 2) {
        oi = i;
        no++;
      }
      else {
        ne++;
      }
    }

    // 入力には奇数が必ず存在する。存在しないならgcdが2になってしまうため。

    // 偶数/奇数 = 偶数、奇数/奇数 = 奇数
    // 奇数で割っても偶奇は入れ替わらない。gcd が奇数のときは、割り算が発生しても偶奇は入れ替わらない。
    // 負け状態 = all 1 = 偶数が 0 個。偶数がある状態は、負け状態ではない。

    // 偶数が奇数個あれば、自分のターンで常に偶数が奇数個ある状態を維持できるため、必勝
    if (ne % 2) {
      if (self) return true;
      else return false;
    }

    // 偶数が偶数個の場合、相手に渡すときに、ある一つの数字の偶奇が入れ替わるので、
    // 偶数が奇数個の必勝パターンを相手に渡してしまうため、必敗。
    // ただし、奇数が1つの場合を除く。
    if (no >= 2) {
      if (self) return false;
      else return true;
    }

    // 偶数が偶数個あり、奇数が1つだけ存在するパターン
    // ただひとつの奇数をdecすると、全部が偶数になり、gcdが偶数になり、Aの偶奇が入れ替わる可能性がある

    if (A[oi] == 1) {
      // ただひとつの奇数が1なので、減らせないので、必敗状態
      if (self) return false;
      else return true;
    }
    A[oi]--;

    // gcd を求めて、割る
    int g = A[0];
    forr(a, A) g = __gcd(g, a);
    forr(a, A) a /= g;

    self = !self;
  }
}

