bool F(vector<ll> a){
    ll n = a.size();
    ll S = 0;
    for(ll i = 0; i < n; i++)
        S += a[i] - 1;
    if(S % 2 == 1) return 1;
    if(n % 2 == 0) return 0;
    ll X = S + n - 1;
    ll Y = 1;
