bool solve(int n, int *a) {
    if (n == 1) return false;
    if ((accumulate(a, a+n, 0LL) + n) % 2) return true;
    if (n % 2 == 0) return false;
    if (find(a, a+n, 1) != a+n) return false;
    int c = 0;
    for (int i = 0; i < n; i++) if (a[i] % 2 == 1) c++;
    if (c > 1 || c == 0) return false;
    for (int i = 0; i < n; i++) if (a[i] % 2 == 1) a[i]--;
    int g = a[0];
    for (int i = 1; i < n; i++) g = gcd(g, a[i]);
    for (int i = 0; i < n; i++) a[i] /= g;
    return !solve(n, a);
}

bool test(int n, const int *a) {
    map<vector<int>, bool> cache;
    function<bool(vector<int> &)> solve = [&](vector<int> &v) {
        if (auto it = cache.find(v); it != cache.end())
            return it->second;
        for (int i = 0; i < v.size(); i++) {
            if (v[i] > 1) {
                vector<int> w = v;
                w[i]--;
                int g = w[0];
                for (int j = 0; j < w.size(); j++)
                    g = gcd(g, w[j]);
                if (g > 1) for (int & x : w) x /= g;
                if (!solve(w)) return cache[v] = true;
            }
        }
        return cache[v] = false;
    };
    auto vec = vector<int>(a, a+n);
    return solve(vec);
}

