//judge(v):=配列の状態がvであるときに手番で勝てるなら1,負けるなら0
int judge(vector<int> v){
    int g = v[0];
    for(auto &e: v) g = gcd(g, e);
    for(auto &e: v) e /= g;
    int cnt = 0, sum = 0;
    for(auto &e: v) sum += e, cnt += e&1;
    if(sum%2 == 0){
        if(N%2 == 0) return 0;
        else return 1;
    }
    else{
        if(N%2 == 0) return 1;
        elif(cnt > 1) return 0;
        else{
            for(auto &e: v) if(e&1) e--;
            return judge(v)^1;
        }
    }
}

