bool dfs(vector<ll>&a){
    int n = a.size();
    vector<ll>p(n),q(n);
    ll sm = 0;
    for(int i=0;i<n;i++){
        sm += a[i];
        if(i==0){
            p[i] = a[i];
        }else{
            p[i] = gcd(p[i-1],a[i]);
        }
    }
    for(int i=n-1;i>=0;i--){
        if(i==n-1){
            q[i] = a[i];
        }else{
            q[i] = gcd(q[i+1],a[i]);
        }
    }
    bool flag = 0;
    rep(i,n){
        if(a[i]==1)continue;
        if(i==0){
            ll g = gcd(q[1],a[0]-1);
            ll x = (sm-1)/g;
            if((x-n)%2==0){
                if(g!=1){
                    vector<ll>b(n);
                    rep(j,n){
                        if(j!=i)b[j] = a[j]/g;
                        else b[j] = (a[j]-1)/g;
                    }
                    if(!dfs(b)){
                        flag = 1;
                    }
                }else{
                    flag = 1;
                }
            }
        }else if(i==n-1){
            ll g = gcd(p[n-2],a[n-1]-1);
            ll x = (sm-1)/g;
            if((x-n)%2==0){
                if(g!=1){
                    vector<ll>b(n);
                    rep(j,n){
                        if(j!=i)b[j] = a[j]/g;
                        else b[j] = (a[j]-1)/g;

                    }
                    if(!dfs(b)){
                        flag = 1;
                    }
                }else{
                    flag = 1;
                }
            }
        }else{
            ll g = gcd(p[i-1],a[i]-1);
            g = gcd(g,q[i+1]);
            ll x = (sm-1)/g;

            if((x-n)%2==0){
                if(g!=1){
                    vector<ll>b(n);
                    rep(j,n){
                        if(j!=i)b[j] = a[j]/g;
                        else b[j] = (a[j]-1)/g;
                    }
                    if(!dfs(b)){
                        flag = 1;
                    }
                }else{
                    flag = 1;
                }
            }
        }
    }
    return flag;
}

