                g = now;
                id = i;
                break;
            }
        }
        if(id==-1) print();
        ll s = 0;
        for(int i=0;i<N;i++){
            if(i!=id) A[i] /= g;
            else A[i] = (A[i]-1)/g;
            s += A[i];
        }
        if((s-N)%2){
            print();
        }
        win ^= true;
        return true;
    };

    while(update())
    print();
}

