int f(int turn, LL sum, vector<LL> A){
    int one=0,two=0,odd=0,O=-1;
    int N=A.size();
    REP(i,N){
        if(A[i]==1)one++;
        else if(A[i]%2==0)two++;
        else odd++,O=i;
    }
    if(one){
        if((sum-N)%2==1)return turn;
        else return 1-turn;
    }
    else if(odd>=1&&(sum-N)%2==1)return turn;
    else if(odd==1){
        LL g=A[O]-1;
        REP(i,N)if(i!=O)g=gcd(g,A[i]);
        vector<LL> B=A;
        B[O]--;
        REP(i,N)B[i]/=g;
        return 1-f(1-turn,(sum-1)/g,B);
    }
    else return 1-turn;
}
