ll f(ll n, vll& a){
    ll odd = 0;
    ll even = 0;
    ll oddi = -1;
    REP(i,0,n-1){
        if(a[i] % 2){
            odd++;
            oddi = i;
        }else{
            even++;
        }
    }
    if(even % 2 == 1){
        return 0;
    }else if(odd >= 2){
        return 1;
    }else{
        if(a[oddi] == 1){
            return 1;
        }
        a[oddi]--;
        ll tmp = gcd(a[0], a[1]);
        REP(i,2,n-1){
            tmp = gcd(tmp, a[i]);
        }
        REP(i,0,n-1){
            a[i] /= tmp;
        }
        return 1 + f(n, a);
    }
}

