		if(cnt&1) assert(can_win);
		else{
			cnt=0;rep(i,1,n) cnt+=a[i]%2;
			if(cnt>1) assert(!can_win);
			else{
				int x=0;rep(i,1,n) if((a[i]>1)&&a[i]%2==1) x=i;
