int a[N], n;
int getAns(int fna = 0) {
	int od = 0, ev = 0, gd = 0;
	for (int i = 0; i < n; i++)
		od += (a[i] % 2), ev += 1 - (a[i] % 2);
	if (ev % 2 == 1)
		return fna + 1;
	if (od > 1)
		return fna;
	for (int i = 0; i < n; i++)
		a[i] -= (a[i] % 2), gd = __gcd(gd, a[i]);
	for (int i = 0; i < n; i++)
		a[i] /= gd;
	return getAns(1) + fna;
}	
