#define sz(c) (int)(c).size()
#define ALL(c) (c).begin(), (c).end()

int work (vi a)
{
    const int n = sz(a);
    if (n == 1)
    {
        assert(a[0] == 1);
        return 0;
    }

    ll sum = 0;
    int cnt = 0;
    bool was = false;
    for (int x : a)
    {
        sum += x;
        cnt += x % 2;
        was |= (x == 1);
    }

    if (was)
        return (sum - n) % 2 == 0 ? 0 : +1;

    assert(cnt > 0);
    if (cnt >= 2)
        return (sum - n) % 2 == 0 ? 0 : +1;

    int pos = -1;
    forn (i, n) if (a[i] % 2) pos = i;
    assert(pos != -1);
    if (a[pos] == 1)
        return 0;

    a[pos]--;
    int g = 0;
    forn (i, n) g = __gcd(g, a[i]);
    assert(g % 2 == 0);
    forn (i, n) a[i] /= g;
    return 1 - work(a);
}

void solve (int n)
{
    vi a(n);
    forn (i, n)
        cin >> a[i];

    int res = work(a);
    cout << (res == +1 ? "First" : "Second") << endl;
}

