using namespace std;

int simulator(vector<long>& cur) {
  long sum = 0;
  for(int i = 0; i < cur.size(); i++) {
    sum += cur[i] - 1;
  }
  bool win = (sum & 1);
  int odds = 0;
  for(int i = 0; i < cur.size(); i++) {
    if(cur[i] == 1) return win;
    if(cur[i] & 1) odds++;
  }
  // 奇数が2つあると勝負は動かない
  if(odds >= 2) return win;
  // 奇数が1つで自分のターンなら勝負は動かない
  if(odds >= 1 && win) return win;
  // 奇数は1つで負け確定 → 勝負するしかない
  // TODO: 例外: N = 2 もう片方を奇数にすると今度は相手が偶数にするしかない
  for(int i = 0; i < cur.size(); i++) {
    if(cur[i] & 1) {
      cur[i]--;
      long gcd = cur[0];
      for(int i = 0; i < cur.size(); i++) {
        gcd = std::__gcd(cur[i], gcd);
      }
      for(int i = 0; i < cur.size(); i++) {
        cur[i] /= gcd;
      }
      return !simulator(cur);
      break;
    }
  }
}
int main() {
  int N;
  cin >> N;
  vector<long> a(N);
  long sum = 0;
  for(int i = 0; i < N; i++) {
    cin >> a[i];
    sum += a[i];
  }
  cout << (simulator(a) ? "First" : "Second") << endl;
  return 0;
}

