int N;

int countOdd(const vector<lint> &A)
{
    int n = 0;
    for (auto v : A) if (v & 1) n++;
    return n;
}

void waru(vector<lint> &A)
{
    lint gcd = A[0];
    for (auto v : A) gcd = __gcd(gcd, v);
    for (auto &v : A) v /= gcd;
}

lint calcRes(const vector<lint> &A)
{
    lint res = -A.size();
    for (auto v : A) res += v;
    return res;
}

int main()
{
    cin >> N;
    vector<lint> A(N);
    cin >> A;
    sort(A.begin(), A.end());

    for (int turn = 0; ; turn++)
    {
        lint yo = calcRes(A);
        int oddNum = countOdd(A);

        if (yo & 1 || A[0] == 1 || oddNum > 1)
        {
            cout << ((yo + turn) & 1 ? "First" : "Second") << endl;
            return 0;
        }
        else
        {
            for (auto &v : A) v /= 2;
            waru(A);
        }
    }
}

#include <bits/stdc++.h>
using namespace std;

int win(bool a) {
    if (a) cout << "First\n";
    else cout << "Second\n";
    return 0;
}

int lose(bool a) {
    return win(!a);
}

int main() {
    cin.tie(nullptr);
    ios::sync_with_stdio(false);
    int n; cin >> n;
    vector<int> a(n);
    for (int i = 0; i < n; i++) cin >> a[i];
    if (n == 1) {
        if (a[0] != 1) return win(1);
        else return lose(1);
    }
    int cnt = 0;
    while (++cnt) {
        int sum = 0; bool one = false;
        for (int i = 0; i < n; i++) {
            sum += a[i] & 1;
            one |= a[i] == 1;
        }
        if (one) break;
        if ((sum & 1) != (n & 1)) return win(cnt&1);
        else if (sum > 1) return lose(cnt&1);
        while (true) {
            int s = 0;
            for (int i = 0; i < n; i++) {
                a[i] /= 2;
                s += a[i] & 1;
            }
            if (s) break;
        }
    }
    int y = 0;
    for (int i = 0; i < n; i++) y = (y + a[i]) & 1;
    if ((n & 1) != y) return win(cnt&1);
    else return lose(cnt&1);
    return 0;
}

