bool rjesi () {
    llint sum = 0, cnt = 0, mn = 2;
    for (int i = 0; i < n; i++) sum += a[i], cnt += a[i] % 2, mn = min(mn, a[i]);
    if ((sum - n) % 2 == 1) return 1;
    if (cnt != 1 || mn == 1) return 0;
    for (int i = 0; i < n; i++) a[i] /= 2;
    return !rjesi();
}

