bool solve(vector<int> a) {
	int N = a.size();
	if (N == 1) return false;
	ll sum = 0;
	rep(i, N) sum += a[i] - 1;
	if (sum % 2) return true;
	int odd = 0;
	rep(i, N) if (a[i] % 2) odd++;
	if (odd >= 2) return false;
	rep(i, N) if (a[i] % 2) a[i]--;
	int d = 0;
	rep(i, N) d = gcd(d, a[i]);
	rep(i, N) a[i] /= d;
	return !solve(a);
}

