ll check(vector<ll> a){
  ll sum = 0;
  ll odd = 0;
  ll one = 0;
  YYS(w, a){
    sum += w;
    if(w % 2 == 1){
      odd++;
    }
    if(w == 1){
      one++;
    }
  }
  if(one >= 1){
    if(sum % 2 == 0){
      return 0;
    } else {
      return 1;
    }
  }
  if(sum % 2 == 0){
    return 0;
  }
  if(odd > 1){
    return 1;
  }
  assert(odd == 1);
  vector<ll> na(0);
  YYS(w, a){
    if(w % 2 == 1){
      na.pb(w - 1);
    } else {
      na.pb(w);
    }
  }
  ll g = na[0];
  YYS(w, na){
    g = __gcd(g, w);
  }
  REP(i, SZ(na)){
    na[i] /= g;
  }
  return 1 - check(na);
}

