bool solve(vector<int> a){
    ll sum = 0;
    int n = a.size();
    for(auto v : a){
        sum += v;
    }
    if(a[0] == 1){
        if((sum - n) & 1){
            return true;
        }else{
            return false;
        }
    }else{
        if((sum - n) & 1){
            return true;
        }else{
            int odd = 0;
            int pos = 0;
            for(int i = 0;i < n;i++){
                if(a[i] & 1){
                    odd++;
                    pos = i;
                }
            }
            if(odd == 1){
                a[pos]--;
                vector<int> b;
                int g = a[0];
                for(int i = 1;i < n;i++){
                    g = gcd(g, a[i]);
                }
                for(int i = 0;i < n;i++){
                    b.push_back(a[i] / g);
                }
                return !solve(b);
            }else{
                return false;
            }
        } 
    }
}


