        for (int i = 0; i < N; i++) { o |= A[i] == 1; }
        if (o) {
            const ll S = std::accumulate(A.begin(), A.end(), 0LL) - N;
            return S % 2 == 1;
        }
        int even = 0;
        for (int i = 0; i < N; i++) { even += (A[i] % 2 == 0 ? 1 : 0); }
        const int odd = N - even;
        if (even % 2 == 1) {
            return true;
        } else if (odd >= 2) {
            return false;
        } else {
            ll g = 0;
            for (int i = 0; i < N; i++) {
                if (A[i] % 2 == 1) { A[i]--; }
                g = gcd(g, A[i]);
            }
            for (int i = 0; i < N; i++) { A[i] /= g; }
            return not self(self, A);
        }
    };
    std::cout << (ok(ok, A) ? "First" : "Second") << std::endl;
    return 0;
}

