const int MAXN = 100005;

int n;
int a[MAXN];

bool check()
{
	int cnt = 0;
	for (int i = 1;i <= n;i++)
	{
		cnt += (a[i] & 1);
		if (a[i] == 1)
		{
			int sum = 0;
			for (int j = 1;j <= n;j++)
				sum += ((a[j] - 1) & 1);
			return (sum & 1) ^ 1;
		}
	}
	while (!cnt)
	{
		for (int i = 1;i <= n;i++)
			a[i] >>= 1;
		for (int i = 1;i <= n;i++)
			cnt += (a[i] & 1);
	}
	int step = ((n - cnt) & 1);
	if (cnt > 1 && !step)
		return 1;
	if (cnt >= 1 && step)
		return 0;
	for (int i = 1;i <= n;i++)
		if (a[i] & 1)
			a[i]--;
	return check() ^ 1;
}

