{
	scanf("%d",&n);
	for (int i = 1;i <= n;i++)
		scanf("%d",&a[i]);
	if (n == 1)
	{
		puts("Second");
		return 0;
	}
	puts(check() ? "Second" : "First");
    return 0;
}

