int solve(vector<int> a)
{
	int odd = 0 , even = 0;

	for(auto x : a)
	{
		if(x % 2)
			odd++;
		else
			even++;
	}

	if(even == 0)
		return odd&1;

	if(odd == 1)
	{
		for(auto &x : a)
			if(x % 2 == 1)
				x--;
		int g = a[0];

		for(auto x : a)
			g = __gcd(g , x);

		for(auto &x : a)
			x /= g;

		return !solve(a);
	}

	return !(even % 2);
}

