            for (int i = 0; i < n; i++) s += a[i] & 1;
            if (s) break;
            for (int i = 0; i < n; i++) a[i] /= 2;
        }
        y = 1 - y;
        bool end = false;
        for (int i = 0; i < n; i++) end |= (a[i] == 1);
        if (end) break;
    }
    if (x != y) return win();
    else lose();
