				return true;
			a[i]--;
		}
		int g = 0;
		for (int i = 0; i < n; i++)
			g = __gcd(g, a[i]);
		for (int i = 0; i < n; i++)
			a[i] = a[i] / g;
	}
	b = !b;
	return false;
}

