bool Dfs(int x)
{
	int odd=0,even=0;
	for (int i=1; i<=n; ++i)
		if (a[i]%2) {a[i]--; break;}
	int g=a[1];
	for (int i=2; i<=n; ++i) g=gcd(g,a[i]);
	for (int i=1; i<=n; ++i)
	{
		a[i]/=g;
		if (!(a[i]%2)) even++;
		else odd++;
		if (a[i]==1) return x;
	}
	if (even%2) return !x;
	if (odd!=1) return x;
	return Dfs(x^1);
}

