  if(p == 0) {
    C[r] = 0;
    LL maxim = 0;
    for(auto &elem: adj[r]) {
      maxim = max(maxim, C[elem]);
    }
    if(maxim*2 > 2ll*arr[r] or sum != 2*arr[r]) {
      ok = false;
    }
  } else if(leaf) {
    C[r] = arr[r];
  } else {
    C[r] = 2ll*arr[r] - sum;
    if(C[r] < 0) {
      ok = false;
      return ;
    }
    LL maxim = C[r];
    for(auto &elem: adj[r]) {
      if(elem != p) {
        maxim = max(maxim, C[elem]);
      }
    }
    if(maxim*2 > 2ll*arr[r]) {
      ok = false;
    }
  }
}

