  for(int i = 1; i < n; i++) {
    cin >> a >> b;
    adj[a].push_back(b), adj[b].push_back(a);
    deg[a]++, deg[b]++;
    if(deg[a] > 1) root = a;
    else if(deg[b] > 1) root = b;
  }
  if(root == -1) {
    if(C[1] == C[2]) cout << "YES" << endl;
    else cout << "NO" << endl;
    return 0;
  }
  dfs(root, 0);
  if(ok) cout << "YES" << endl;
  else cout << "NO" << endl;
  return 0;
}

