using i64 = long long;

i64 gcd(i64 a, i64 b) { return b ? gcd(b, a % b) : a; }

bool rec(std::vector<i64> &v, bool first = true) {
    const i64 sum = std::accumulate(v.begin(), v.end(), 0ll);
    const int n = v.size();
    const int even = std::count_if(v.begin(), v.end(), [](i64 k) { return k % 2 == 0; });
    if ((sum - n) % 2) return first;
    else if (n - even != 1 || n == 1) return !first;

    for (auto &e : v) if (e % 2) {
        --e;
        break;
    }
    i64 g = std::accumulate(v.begin(), v.end(), 0ll, gcd);
    for (auto &e : v) e /= g;
    return rec(v, !first);
}

