int gcd(int x, int y) {
  while (x) {
    y %= x;
    swap(x, y);
  }
  return y;
}

int N;
vector<int> a;

bool solve() {
  bool lost = true;
  for (int i = 0; i < N; ++i) {
    if (a[i] != 1) {
      lost = false;
    }
  }

  if (lost) {
    return false;
  }

  int cnt = 0;
  for (int i = 0; i < N; ++i) {
    if (a[i] % 2 == 0) {
      ++cnt;
    }
  }

  if (cnt & 1) {
    return true;
  }

  if (N - cnt > 1) {
    return false;
  }

  for (int i = 0; i < N; ++i) {
    if (a[i] % 2 == 1) {
      if (a[i] == 1) {
        return false;
      }
      --a[i];
      break;
    }
  }

  int g = a[0];
  for (int i = 0; i < N; ++i) {
    g = gcd(g, a[i]);
  }

  for (int i = 0; i < N; ++i) {
    a[i] /= g;
  }

  return !solve();
}

int main() {
  ios_base::sync_with_stdio(false);
  cin.tie(nullptr);

  cin >> N;
  a.resize(N);
  for (int i = 0; i < N; ++i) {
    cin >> a[i];
  }

  if (solve()) {
    cout << "First" << endl;
  } else {
    cout << "Second" << endl;
  }
}

