bool solve(int n, VI &a) {
    if (n == 1) {
        return false;
    }

    int sum = 0;
    FOR(i,0,n-1) {
        sum = (sum + a[i]) % 2;
    }

    if (1 - sum == n % 2) {
        return true;
    }

    bool found = false;
    FOR(i,0,n-1) if (a[i] % 2 && a[i] > 1) {
        found = true;
        a[i]--;

        break;
    }

    if (!found) {
        return false;
    }

    int gcd = 0;
    FOR(i,0,n-1) {
        gcd = __gcd(gcd, a[i]);
    }

    FOR(i,0,n-1) {
        a[i] /= gcd;
    }

    return !solve(n, a);
}

