    if (sum[0] % 2 == 1 && sum[1] >= 1) return 1; 
    if (sum[0] % 2 == 0 && (sum[1] >= 2 || !sum[1])) return 2;
    return -1;
}

inline void operates() {
    for (int i = 1; i <= n; ++i)
        if (a[i] % 2) --a[i];
    int g = a[1];
    for (int i = 2; i <= n; ++i)
        g = __gcd(g, a[i]);
    for (int i = 1; i <= n; ++i)
        a[i] /= g;
}

bool dfs() {
    int tmp = 0;
    if (tmp = check()) return (tmp == 1);
    return operates(), !dfs();
}

