bool gd() {
	if (n % 2 == 0)
		return true;
	int res = 0;
	for (int i = 0; i < n; i++)
		res += a[i] % 2;
	if (res >= 2)
		return true;
	for (int i = 0; i < n; i++) {
		if (a[i] % 2) {
			if (a[i] == 1)
				return true;
			a[i]--;
		}
		int g = 0;
		for (int i = 0; i < n; i++)
			g = __gcd(g, a[i]);
		for (int i = 0; i < n; i++)
			a[i] = a[i] / g;
	}
	b = !b;
	return false;
}

