		cout << ((1 - turn) ? "Second" : "First") << endl;
		return;
	}
	int parity = 0;
	for (int i = 0; i < n; i++)
		parity ^= (a[i] & 1);
	if (n & 1)
	{
		if (!parity)
		{
			cout << (turn ? "Second" : "First") << endl;
			return;
		}
		else
		{
			int cnt = 0;
			for (int i = 0; i < n; i++)
				cnt += (a[i] & 1);
			if (cnt > 1)
			{
				cout << ((1 - turn) ? "Second" : "First") << endl;
				return;
			}
			else
			{
				for (int i = 0; i < n; i++)
					if (a[i] & 1)
						a[i]--;
				int GCD = a[0];
				for (int i = 1; i < n; i++)
					GCD = __gcd(a[i], GCD);
				for (int i = 0; i < n; i++)
					a[i] /= GCD;
				solve(1 - turn);
			}
		}
	}
	else
	{
		if (!parity)
			cout << ((1 - turn) ? "Second" : "First") << endl;
		else
			cout << (turn ? "Second" : "First") << endl;
	}
}

