		if (even % 2 == 0) {
			if (odd > 1) return !isA;
			REP(i, n) {
				if (a[i] == 1) return !isA;
				if (a[i] % 2 == 1) {
					--a[i];
					break;
				}
			}
			int g = a[0];
			FOR(i, 1, n) g = gcd(g, a[i]);
			REP(i, n) a[i] /= g;
			return rec(!isA);
		}
		else {
			return isA;
		}
	};

	OUT((rec(true) ? "First" : "Second"))BR;
	return 0;
}

