int f(vector<LL>& v, int player) {
  LL s = 0;
  for (auto x : v) s += x;
  if ((n & 1) ^ (s & 1)) return player;
  if (s == n) return player ^ 1;
  int cnt = 0, idx = -1;
  rep(i, n) if (v[i] % 2 == 0) {
